import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import { Link } from 'react-router-dom';
import Head from './common/Head';
import Down from './common/Down';
import { useState } from 'react';

function App() {
  const[data, setdata] =useState(10)
  console.log(data);

  const[name,setname]=useState("hello")
  console.log(name);

  const handle_Increment = () => {
    setdata(data + 1)
  }

  const handle_Decrement = () =>{
    setdata(data - 1)
  }

  const[input1,setinput1] = useState('');
  const[input2,setinput2] = useState('');

  const handledata = () =>{
    if(input1 === ''){
      alert("Please enter your email")
    }
    else if(input2 === ''){
      alert("please enter your password")
    }
    else{
      console.log(input1,input2);
    }

  }

  return (
    <div className="App">
      <Head/>
      <div className='row'>
      <div className='col-6'>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>

      </header>
      </div>
      <div className='col-6'>
        <h1>{name}{data}
          <button onClick={handle_Increment}>ADD</button>
          <button onClick={handle_Decrement}>Sub</button>
        </h1>
        <button>Go to home</button><br/>
        <label>Email</label>
        <input 
        type='text' 
        value={input1} 
        onChange={(e)=>setinput1(e.target.value)}/>
        <label>Password</label>
        <input 
        type='Password' 
        value={input2}
        onChange={(e)=>setinput2(e.target.value)}
         />
         <button onClick={handledata}>SUBMIT</button><br/>
         <label>{input1}</label>
        <Link to="/home">
        </Link>
      </div>
      </div>
      <Down/>
    </div>
  );
}

export default App;
